mapper.insert:
INSERT INTO
sdp.tb_attach_file(
    reference_id
    , file_name
    , file_path
    , create_dt
    , table_name
)
VALUES(
    #{reference_id}
    , #{file_name}
    , #{file_path}
    , now()
    , #{table_name}
)

mapper.update:
UPDATE sdp.tb_attach_file
SET
    reference_id = #{reference_id}
    , file_name = #{file_name}
    , file_path = #{file_path}
    , table_name = #{table_name}
WHERE attach_file_id = #{attach_file_id}::int

mapper.delete:
DELETE FROM sdp.tb_attach_file
WHERE reference_id = #{reference_id}
    and table_name = #{table_name}
