mapper.update:
UPDATE sdp.tb_codegroup
SET
    codegroup_name = #{codegroup_name}
    , code_type = #{code_type}
    , description = #{description}
    , create_id = #{create_id}::int
    , update_id = #{update_id}::int
    , update_dt = now()
    , del_yn = #{del_yn}
WHERE codegroup_id = #{codegroup_id}::int
