mapper.insert:
INSERT INTO
saehan.tb_facility(
    facility_id
    , branch_id
    , type
    , room_name
    , floor
    , acceptor_number
    , basic_use_hour
    , basic_use_fee
    , state
    , created_date
    , del_yn
    , update_date
    , operation_open_time
    , operation_close_time
)
VALUES(
    #{facility_id}::int
    , #{branch_id}::int
    , #{type}
    , #{room_name}
    , #{floor}
    , #{acceptor_number}::int
    , #{basic_use_hour}::int
    , #{basic_use_fee}::int
    , #{state}
    , #{created_date}
    , #{del_yn}
    , #{update_date}
    , #{operation_open_time}
    , #{operation_close_time}
)

mapper.update:
UPDATE saehan.tb_facility
SET
    branch_id = #{branch_id}::int
    , type = #{type}
    , room_name = #{room_name}
    , floor = #{floor}
    , acceptor_number = #{acceptor_number}::int
    , basic_use_hour = #{basic_use_hour}::int
    , basic_use_fee = #{basic_use_fee}::int
    , state = #{state}
    , created_date = #{created_date}
    , del_yn = #{del_yn}
    , update_date = #{update_date}
    , operation_open_time = #{operation_open_time}
    , operation_close_time = #{operation_close_time}
WHERE facility_id = #{facility_id}::int
