<node CREATED="1526359958332" ID="ID_931615208" MODIFIED="1526359960909" TEXT="public int compare(PersonVo o1, PersonVo o2) {">
<node CREATED="1526359958332" MODIFIED="1526359960909" TEXT="int ret = 0;"/>
<node CREATED="1526359958332" MODIFIED="1526359960909" TEXT="if(sort_field == SORT_FIELD_NAME) {">
<node CREATED="1526359958332" ID="ID_453132535" MODIFIED="1526359960909" TEXT="if(o1.getName().compareTo(o2.getName())&gt;0) ret = 1;"/>
<node CREATED="1526359958332" ID="ID_725455087" MODIFIED="1526359960909" TEXT="else if(o1.getName().compareTo(o2.getName())&lt;0) ret = -1;"/>
</node>
<node CREATED="1526359958332" MODIFIED="1526359960909" TEXT="}"/>
<node CREATED="1526359958332" MODIFIED="1526359960909" TEXT="else if(sort_field == SORT_FIELD_AGE)"/>
<node CREATED="1526359958332" MODIFIED="1526359960909" TEXT="{">
<node CREATED="1526359958332" MODIFIED="1526359960909" TEXT="if(o1.getAge() &gt; o2.getAge()) ret = 1;"/>
<node CREATED="1526359958332" MODIFIED="1526359960909" TEXT="else if(o1.getAge() &lt; o2.getAge()) ret =-1;"/>
</node>
<node CREATED="1526359958332" MODIFIED="1526359960909" TEXT="}"/>
<node CREATED="1526359958332" MODIFIED="1526359960909" TEXT="if(sort_type == SORT_TYPE_DESC)">
<node CREATED="1526359958332" MODIFIED="1526359960909" TEXT="ret = - ret;"/>
</node>
<node CREATED="1526359958332" MODIFIED="1526359960909" TEXT="return ret;"/>
</node>
<node CREATED="1526359958332" ID="ID_1383732800" MODIFIED="1526359960909" TEXT="}"/>
</node>
<node CREATED="1526359958332" ID="ID_51669459" MODIFIED="1526359960909" TEXT="}"/>
